import React from 'react';
//import ReactDOM from 'react-dom'
//import logo from './logo.svg';
import './App.css';
import {
  Route,
  BrowserRouter as Router,
  Switch,
} from 'react-router-dom'
import Days from './days'
import Form from './form'
//import Notfound from './notfound'


const API_KEY ="1047b4a8bd298fee185e1158ad30ecd1";
class App extends React.Component {
  state={
    day :undefined,
    description: undefined,
    low:undefined,
    high:undefined,
    error:undefined
  }

  getWeather = async (e) => {

    e.preventDefault();
    const city=e.target.elements.city.value;
    //const API_CALL= await fetch('api.openweathermap.org/data/2.5/forecast?q=${city}&appid={API_KEY}&units=metric');

    const API_CALL = await fetch(`api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}&units=metric`);
    const DATA=await API_CALL.json();
    console.log(DATA);
    this.setState({
      day1 :DATA.list[0].dt,
      description:DATA.list[0].weather.description,
      low:undefined,
      high:undefined,
      error:undefined

    });
  }
  render() {
    return (
      <Router>
      <div>
        <h1>WELCOME TO 5 DAY WEATHER FORECAST</h1>
        <Form getWeather={this.getWeather}/>
       <Switch>
        <Route path="/days" component={Days} 
        day1={this.state.day1}
        description={this.state.description}/>
      </Switch>
      </div>
      </Router>
    )
  }
}
export default App
