   // const forecastTiles = this.props.tiles.length > 5 ? this.props.tiles.slice(0, 5) : this.props.tiles;
   const forecastTiles=this.props.tiles;
    

    return (
        <Router>
          <div>
            <h1>5 Day weather forecast</h1>
           
            <table border='1'>
                <thead>
                <tr>
                    <th>DAY</th>
                    <th>DESCRIPTION</th>
                    <th>Min_Temp</th>
                    <th>Max_temp</th>
                    <th>ICON</th>
                </tr></thead>
                <tbody>
         
                {forecastTiles.map((item) =>  (<tr>
                   { console.log(item)}
                      <td > <Link to={{
                        pathname :`/days/${this._getDayInfo(item)}`,
                        query: {
                            
                            comments: JSON.stringify(item)
                          } }
}>
                        {this._getDayInfo(item)}</Link></td>
                    <td>{item[0].weather[0].description}</td>
                    {this._getInfo(item)};
                    <td><img src={`https://openweathermap.org/img/w/${item[0].weather[0].icon}.png`} alt=""></img></td>
                </tr>) )
                }
                  </tbody>
            </table>
            
    <Route path="/days/:id" component={DetailedInfo} />
          </div>
          </Router>
    );
    }
            
}
      
