import React from 'react';
//import ReactDOM from 'react-dom'
//import logo from './logo.svg';
import './App.css';
import {
  Route,
  BrowserRouter as Router,
  Switch,
} from 'react-router-dom'
import Days from './days'
import Form from './form'
//import Notfound from './notfound'


const API_KEY ="1047b4a8bd298fee185e1158ad30ecd1";
class App extends React.Component {
  state={
    temp_min: undefined,
    temp_max: undefined,
    city: undefined,
    description: undefined,
    icon: undefined,
    day :undefined,
    error : undefined
  }

  getWeather = async (e) => {

    e.preventDefault();
    const city=e.target.elements.city.value;

    const API_CALL = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}&units=metric`); 

    //const API_CALL = await fetch(`http://pro.openweathermap.org/data/2.5/forecast/hourly?q=${city}&appid=${API_KEY}`);
    const data=await API_CALL.json();

    console.log(data);
    this.setState={
      temp_min:data.list[0].main.temp_min ,
      temp_max: data.list[0].main.temp_max ,
      city: undefined,
      description: undefined,
      icon: undefined,
      day :undefined,
      error : undefined
    }

  }
  render() {
    return (
      <Router>
      <div>
        <h1>WELCOME TO 5 DAY WEATHER FORECAST</h1>
        <Form getWeather={this.getWeather}/>
       <Switch>
        <Route path="/days" component={Days} />
      </Switch>
      </div>
      </Router>
    )
  }
}
export default App
