import React from 'react'
//import { Route, Link} from 'react-router-dom'

//const   Day=({match}) => <p> {match.params.id}</p>
//var x=props.city;
//alert(x);
const Days = props => (
//class Days extends React.Component{
   // render(){
        //const { url } = this.props.match
       
          <div>
            <h1>5 Day weather forecast</h1>
           
            <table border='1'>
                <thead>
                <tr>
                    <th>DAY</th>
                    <th>DESCRIPTION</th>
                    <th>Min_Temp</th>
                    <th>Max_temp</th>
                    <th>ICON</th>
                </tr></thead>
                <tbody>
             
    {props.dailyData}.map((day, index) =>(
              <tr>
                  <td>{ new Date(day.dt * 1000).toDateString()}</td>
                  <td>{day.weather[0].description}</td>
                  <td>{day.main.temp_min}</td>
                  <td>{day.main.temp_max}</td>
                  <td><img src={`http://openweathermap.org/img/w/${day.weather[0]}.png`} alt="" ></img></td>
              </tr>
    ))
              </tbody>
            </table>
            
          </div>
        );
      
    export default Days
    //<Route path="/days/:id" component={Days} />
