getHour = time => time ? new Date(time).getHours() : new Date().getHours();
  getDate = date => date ? new Date(date).getDate() : new Date().getDate();

displayMoreInfo = (item, i) => {
    return (
      <div className="hourly-info" key={i}>
        <div className="hour-temperature">
          {`${Math.round(item.main.temp)}°C`}
        </div>
        <div className="hour-of-the-day">
          {`${getHour(item.dt * 1000)}:00`}
        </div>
      </div>
    );
  };
  
    render(){
        const data=this.props.data;
        const d=() => {
        const daysOfWeek = ["sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"];
      

       return data.filter(reading => daysOfWeek[new Date(reading[0].dt * 1000).getDay()].includes(props.match.params.id));}
       // const d=this.state.tiles;
  return (
    <div >
     {console.log(data)} ;
     {console.log(d)};
      {d.map((item, i) => (
        (getHour(item.dt * 1000) > getHour() && getDate(item.dt * 1000) === getDate()) ? (
          displayMoreInfo(item, i)
         ) : getHour(item.dt * 1000) >= 5 && getHour(item.dt * 1000) <= 23 ? (
            displayMoreInfo(item, i)
         ) : null
      ))}
    </div>
  );
}
}
