   // const {data}=this.props.location.state;
     displayMoreInfo = (item, i) => {
        return (
            <p>
              {`${Math.round(item.main.temp)}°C`}
           
              {`${getHour(item.dt * 1000)}:00`}
            </p>
        );
      };
      
       getHour = time => {return(time ? new Date(time).getHours() : new Date().getHours());};
     getDate = date =>{return(date ? new Date(date).getDate() : new Date().getDate());};
    render(){
        const {data}=this.props.location.myCustomProps;
  return (
    <div >
      {data.map((item, i) => (
        (getHour(item.dt * 1000) > getHour() && getDate(item.dt * 1000) === getDate()) ? (displayMoreInfo(item, i) ) : 
        getHour(item.dt * 1000) >= 5 && getHour(item.dt * 1000) <= 23 ? (
            displayMoreInfo(item, i) ) : null))}
    </div>
  );}}
