import React from "react";

import ReactDOM from 'react-dom';

class DetailedInfo extends React.Component
{
    constructor(props){
        super(props);
        console.log(this.props);
    }

    //const {handle}= this.props.match.params;
   // const {data}=this.props.location.state;
     displayMoreInfo = (item, i) => {
        return (
          <div key={i}>
            <div >
              {`${Math.round(item.main.temp)}°C`}
            </div>
            <div>
              {`${getHour(item.dt * 1000)}:00`}
            </div>
          </div>
        );
      };
      
       getHour = time => time ? new Date(time).getHours() : new Date().getHours();
     getDate = date => date ? new Date(date).getDate() : new Date().getDate();
    render(){
        const {data}=props.location.state;
  return (
    <div >
      {data.map((item, i) => (
        (getHour(item.dt * 1000) > getHour() && getDate(item.dt * 1000) === getDate()) ? (displayMoreInfo(item, i) ) : 
        getHour(item.dt * 1000) >= 5 && getHour(item.dt * 1000) <= 23 ? (
            displayMoreInfo(item, i) ) : null))}
    </div>
  );}}
