import React from 'react'
import { Route, Link,BrowserRouter as Router} from 'react-router-dom'
//import DetailedInfo from './detailedInfo'

const   DetailedInfo=({match}) => <div> {match.params.id}</div>
//var x=props.city;
//alert(x);
//const Days = props => (
class Days extends React.Component{
  
constructor(props){
    super(props);
    console.log(this.props);
    /*this.state={
        data: []
      }*/
}
      _getDayInfo = data => {
        const daysOfWeek = ["sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"];
        return daysOfWeek[new Date(data[0].dt * 1000).getDay()];
      };


  _getInfo = (data) => {
    const min=[];
     const max=[];
    data.map(item => {
      max.push(item.main.temp_max);
      min.push(item.main.temp_min);
    });

    const minMax = {
      min: Math.round(Math.min(...min)),
      max: Math.round(Math.max(...max)),
    };
  return (
    <td>{`${minMax.min} and ${minMax.max}`}</td>
  );}

  
  getHour = (time) => {return(time ? new Date(time).getHours() : new Date().getHours());};
  getDate = (date) =>{return(date ? new Date(date).getDate() : new Date().getDate());};
  displayMoreInfo = (item, i) => {
    return (
        <p>
          {`${Math.round(item.main.temp)}Â°C`}
       
          {`${getHour(item.dt * 1000)}:00`}
        </p>
    );
  };
  

    render(){
        //const { url } = this.props.match

        

    // Edge case:
    // When the webservice returns data for 6 calendar days during evenings as a result of offset,
    // this ensures that we are showing only 5-days of forecast.
    
   // const forecastTiles = this.props.tiles.length > 5 ? this.props.tiles.slice(0, 5) : this.props.tiles;
   const forecastTiles=this.props.tiles;
    

    return (
        <Router>
          <div>
            <h1>5 Day weather forecast</h1>
           
            <table border='1'>
                <thead>
                <tr>
                    <th>DAY</th>
                    <th>DESCRIPTION</th>
                    <th>Min_Temp</th>
                    <th>Max_temp</th>
                    <th>ICON</th>
                </tr></thead>
                <tbody>
         
                {forecastTiles.map((item) =>  (<tr>
                    <td > <Link to={{
                        pathname :`/days/${this._getDayInfo(item)}`,
                        myCustomProps: item
}}>
                        {this._getDayInfo(item)}</Link></td>
                    <td>{item[0].weather[0].description}</td>
                    {this._getInfo(item)};
                    <td><img src={`https://openweathermap.org/img/w/${item[0].weather[0].icon}.png`} alt=""></img></td>
                </tr>) )
                }
                  </tbody>
            </table>
            
    <Route path="/days/:id" component={DetailedInfo} />
          </div>
          </Router>
    );
    }
            
}
      
    export default Days
